import React, { useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import "./Audiobook.css";
import { toast } from "react-toastify";
import { LoginContext } from "../context/LoginContext";

export default function Audiobook() {
  const navigate = useNavigate();
  const { setUserLogin } = useContext(LoginContext);
  const [audiobooks, setAudiobooks] = useState([]);
  const [loading, setLoading] = useState(true);

  // Toast function for errors
  const notifyError = (msg) => toast.error(msg);
  const notifySuccess = (msg) => toast.success(msg);

  useEffect(() => {
    const checkAuth = () => {
      const token = localStorage.getItem("jwt");
      const user = localStorage.getItem("user");

      console.log("Audiobook - Token:", !!token); // Debug log
      console.log("Audiobook - User:", !!user); // Debug log

      if (!token || !user) {
        console.log("Audiobook - Not authenticated, redirecting to signin"); // Debug log
        notifyError("Please login to access audiobooks");
        navigate("/signin");
        return false;
      }

      setUserLogin(true);
      return true;
    };

    const fetchAudiobooks = async () => {
      if (!checkAuth()) return;

      try {
        const token = localStorage.getItem("jwt");
        console.log("Audiobook - Fetching audiobooks with token"); // Debug log

        const response = await fetch("http://localhost:4000/audiobooks", {
          headers: {
            Authorization: "Bearer " + token,
          },
        });

        if (response.status === 401) {
          console.log("Audiobook - Token expired or invalid"); // Debug log
          localStorage.clear();
          setUserLogin(false);
          notifyError("Session expired. Please login again.");
          navigate("/signin");
          return;
        }

        const result = await response.json();
        console.log("Audiobook - Fetch result:", result); // Debug log
        setAudiobooks(result);
        setLoading(false);
      } catch (err) {
        console.error("Audiobook - Fetch error:", err); // Debug log
        notifyError("Failed to fetch audiobooks");
        setLoading(false);
      }
    };

    fetchAudiobooks();
  }, [navigate, setUserLogin]);

  const handleCardClick = (audiobookId) => {
    navigate(`/audiobook/${audiobookId}`);
  };

  // Format duration from seconds to MM:SS
  const formatDuration = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  if (loading) {
    return <div className="loading">Loading audiobooks...</div>;
  }

  return (
    <div className="audiobook-container">
      <h1 className="audiobook-title">Audiobooks</h1>
      <div className="audiobook-grid">
        {audiobooks.map((book) => (
          <div
            key={book._id}
            className="audiobook-card"
            onClick={() => handleCardClick(book._id)}
          >
            <div className="audiobook-thumbnail">
              <img src={book.thumbnailUrl} alt={book.name} />
            </div>
            <div className="audiobook-details">
              <h3 className="audiobook-name">{book.name}</h3>
              <p className="audiobook-author">By {book.author}</p>
              <div className="audiobook-meta">
                <span className="audiobook-genre">{book.genre}</span>
                <span className="audiobook-duration">
                  {formatDuration(book.duration)}
                </span>
              </div>
              <div className="audiobook-rating">
                {[...Array(5)].map((_, index) => (
                  <span
                    key={index}
                    className={`star ${index < Math.floor(book.rating) ? 'filled' : ''}`}
                  >
                    â˜…
                  </span>
                ))}
                <span className="rating-value">({book.rating.toFixed(1)})</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
