import React, { useState, useContext } from 'react';
import { UserContext } from '../App';
import './ReelUploadModal.css';

const ReelUploadModal = ({ onClose, onUploadSuccess }) => {
  const { state } = useContext(UserContext);
  const [formData, setFormData] = useState({
    title: '',
    caption: '',
    description: '',
    category: '',
    video: null,
    thumbnail: null
  });
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState('');
  const [preview, setPreview] = useState({
    video: null,
    thumbnail: null
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    if (files) {
      const file = files[0];

      // Check video duration if it's a video file
      if (name === 'video' && file) {
        const video = document.createElement('video');
        video.preload = 'metadata';
        video.onloadedmetadata = () => {
          if (video.duration > 150) { // 2.5 minutes in seconds
            setError('Video duration must be less than 2.5 minutes');
            return;
          }
          setFormData(prev => ({
            ...prev,
            [name]: file,
            duration: video.duration
          }));
          const videoUrl = URL.createObjectURL(file);
          setPreview(prev => ({
            ...prev,
            video: videoUrl
          }));
        };
        video.src = URL.createObjectURL(file);
      } else {
        setFormData(prev => ({
          ...prev,
          [name]: file
        }));

        // Create preview for thumbnail
        if (name === 'thumbnail' && file) {
          const reader = new FileReader();
          reader.onloadend = () => {
            setPreview(prev => ({
              ...prev,
              thumbnail: reader.result
            }));
          };
          reader.readAsDataURL(file);
        }
      }
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!state) return;

    setUploading(true);
    setError('');

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('title', formData.title);
      formDataToSend.append('caption', formData.caption);
      formDataToSend.append('description', formData.description);
      formDataToSend.append('category', formData.category);
      formDataToSend.append('video', formData.video);
      formDataToSend.append('thumbnail', formData.thumbnail);
      formDataToSend.append('duration', formData.duration);

      const response = await fetch('http://localhost:4000/api/reel/upload', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${localStorage.getItem('jwt')}`
        },
        body: formDataToSend
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Upload failed');
      }

      onUploadSuccess();
      onClose();
    } catch (err) {
      console.error('Upload error:', err);
      setError(err.message || 'Failed to upload reel. Please try again.');
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Upload Reel</h2>
          <button className="close-button" onClick={onClose}>&times;</button>
        </div>

        <form onSubmit={handleSubmit} className="upload-form">
          <div className="form-group">
            <label htmlFor="title">Title</label>
            <input
              type="text"
              id="title"
              name="title"
              value={formData.title}
              onChange={handleChange}
              required
              placeholder="Enter reel title"
              maxLength={100}
            />
          </div>

          <div className="form-group">
            <label htmlFor="caption">Caption</label>
            <input
              type="text"
              id="caption"
              name="caption"
              value={formData.caption}
              onChange={handleChange}
              required
              placeholder="Enter a catchy caption"
              maxLength={200}
            />
          </div>

          <div className="form-group">
            <label htmlFor="description">Description</label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              required
              placeholder="Enter reel description"
              rows="4"
              maxLength={500}
            />
          </div>

          <div className="form-group">
            <label htmlFor="category">Category</label>
            <select
              id="category"
              name="category"
              value={formData.category}
              onChange={handleChange}
              required
            >
              <option value="">Select a category</option>
              <option value="Music">Music</option>
              <option value="Dance">Dance</option>
              <option value="Comedy">Comedy</option>
              <option value="Food">Food</option>
              <option value="Travel">Travel</option>
              <option value="Fashion">Fashion</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="thumbnail">Thumbnail</label>
            <input
              type="file"
              id="thumbnail"
              name="thumbnail"
              onChange={handleChange}
              accept="image/*"
              required
            />
            {preview.thumbnail && (
              <div className="preview-container">
                <img src={preview.thumbnail} alt="Thumbnail preview" className="thumbnail-preview" />
              </div>
            )}
          </div>

          <div className="form-group">
            <label htmlFor="video">Reel Video (max 2.5 minutes)</label>
            <input
              type="file"
              id="video"
              name="video"
              onChange={handleChange}
              accept="video/*"
              required
            />
            {preview.video && (
              <div className="preview-container">
                <video src={preview.video} controls className="video-preview" />
              </div>
            )}
          </div>

          {error && <div className="error-message">{error}</div>}

          <div className="form-actions">
            <button
              type="button"
              className="cancel-button"
              onClick={onClose}
              disabled={uploading}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="submit-button"
              disabled={uploading}
            >
              {uploading ? 'Uploading...' : 'Upload Reel'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ReelUploadModal;
