import React, { useContext, useState, useEffect } from "react";
import logo from "../img/logo.png";
import "./Navbar.css";
import { Link, useNavigate } from "react-router-dom";
import { LoginContext } from "../context/LoginContext";
import { toast } from "react-toastify";

export default function Navbar({ login }) {
  const { setUserLogin, setModalOpen } = useContext(LoginContext);
  const [menuOpen, setMenuOpen] = useState(false);
  const navigate = useNavigate();

  // Check login status on component mount and when login prop changes
  useEffect(() => {
    const token = localStorage.getItem("jwt");
    const user = localStorage.getItem("user");
    if (token && user) {
      setUserLogin(true);
    }
  }, [login, setUserLogin]);

  const handleAudiobookClick = (e) => {
    e.preventDefault();
    navigate("/audiobooks");
  };

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const loginStatus = () => {
    const token = localStorage.getItem("jwt");
    const user = localStorage.getItem("user");
    console.log("Login status check - Token:", !!token, "User:", !!user); // Debug log

    if (login || (token && user)) {
      return [
        <>
          <li className="menu-container">
            <div className="hamburger-icon" onClick={toggleMenu}>
              <i className="material-icons">menu</i>
            </div>
            {menuOpen && (
              <div className="dropdown-menu">
                <Link to="/createPost" onClick={toggleMenu}>
                  <div className="menu-item">
                    <i className="material-icons">add_circle</i>
                    <span>Create Post</span>
                  </div>
                </Link>
                <Link to="/followingpost" onClick={toggleMenu}>
                  <div className="menu-item">
                    <i className="material-icons">people</i>
                    <span>My Following</span>
                  </div>
                </Link>
                <div 
                  className="menu-item logout-item" 
                  onClick={() => {
                    toggleMenu();
                    setModalOpen(true);
                  }}
                >
                  <i className="material-icons">exit_to_app</i>
                  <span>Log Out</span>
                </div>
              </div>
            )}
          </li>
        </>,
      ];
    } else {
      return [
        <>
          <Link to="/signup">
            <li>Sign Up</li>
          </Link>
          <Link to="/signin">
            <li>Sign In</li>
          </Link>
        </>,
      ];
    }
  };

  return (
    <div className="navbar">
      <h2 className="logo">Vybes</h2>
      <div className="nav-right">
        <div className="nav-buttons">
          <Link to="/ai-bot" className="nav-button">
            <i className="material-icons">smart_toy</i>
            <span>AI Bot</span>
          </Link>
          <a href="#" onClick={handleAudiobookClick} className="nav-button">
            <i className="material-icons">library_music</i>
            <span>Audiobook</span>
          </a>
        </div>
        <ul className="nav-menu">{loginStatus()}</ul>
      </div>
    </div>
  );
}
