import React, { useEffect, useState, useContext } from "react";
import "./MyFollowingPost.css";
import { UserContext } from "../context/UserContext";
import { toast } from "react-toastify";
import config from "../config/config";

export default function MyFollowingPost() {
  const { state } = useContext(UserContext);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      const response = await fetch(`${config.BACKEND_URL}/myfollwingpost`, {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("jwt"),
        },
      });

      if (!response.ok) {
        throw new Error('Failed to fetch posts');
      }

      const result = await response.json();
      setData(result);
    } catch (error) {
      console.error("Error fetching posts:", error);
      toast.error("Failed to load posts");
    } finally {
      setLoading(false);
    }
  };

  const likePost = async (id) => {
    try {
      const response = await fetch(`${config.BACKEND_URL}/like`, {
        method: "put",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + localStorage.getItem("jwt"),
        },
        body: JSON.stringify({
          postId: id,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to like post');
      }

      const result = await response.json();
      console.log(result);
      fetchData(); // Refresh posts
      toast.success("Post liked successfully");
    } catch (error) {
      console.error("Error liking post:", error);
      toast.error("Failed to like post");
    }
  };

  const unlikePost = async (id) => {
    try {
      const response = await fetch(`${config.BACKEND_URL}/unlike`, {
        method: "put",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + localStorage.getItem("jwt"),
        },
        body: JSON.stringify({
          postId: id,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to unlike post');
      }

      const result = await response.json();
      console.log(result);
      fetchData(); // Refresh posts
      toast.success("Post unliked successfully");
    } catch (error) {
      console.error("Error unliking post:", error);
      toast.error("Failed to unlike post");
    }
  };

  const makeComment = async (text, postId) => {
    try {
      const response = await fetch(`${config.BACKEND_URL}/comment`, {
        method: "put",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + localStorage.getItem("jwt"),
        },
        body: JSON.stringify({
          postId: postId,
          text: text,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to add comment');
      }

      const result = await response.json();
      console.log(result);
      fetchData(); // Refresh posts
      toast.success("Comment added successfully");
    } catch (error) {
      console.error("Error adding comment:", error);
      toast.error("Failed to add comment");
    }
  };

  const deletePost = async (postId) => {
    try {
      const response = await fetch(`${config.BACKEND_URL}/deletePost/${postId}`, {
        method: "delete",
        headers: {
          Authorization: "Bearer " + localStorage.getItem("jwt"),
        },
      });

      if (!response.ok) {
        throw new Error('Failed to delete post');
      }

      const result = await response.json();
      console.log(result);
      fetchData(); // Refresh posts
      toast.success("Post deleted successfully");
    } catch (error) {
      console.error("Error deleting post:", error);
      toast.error("Failed to delete post");
    }
  };

  if (loading) {
    return (
      <div className="myFollowingPost">
        <div className="loading">Loading posts...</div>
      </div>
    );
  }

  return (
    <div className="myFollowingPost">
      <div className="post">
        {data && data.length > 0 ? (
          data.map((item) => (
            <div className="post-item" key={item._id}>
              <div className="post-header">
                <div className="post-pic">
                  <img src={item.postedBy?.Photo || "https://cdn-icons-png.flaticon.com/128/3177/3177440.png"} alt="" />
                </div>
                <div className="post-info">
                  <h5>{item.postedBy?.name}</h5>
                  <p>{new Date(item.createdAt).toLocaleDateString()}</p>
                </div>
                {state && item.postedBy && state._id === item.postedBy._id && (
                  <button 
                    className="delete-btn"
                    onClick={() => {
                      if (window.confirm("Do you really want to delete this post?")) {
                        deletePost(item._id);
                      }
                    }}
                  >
                    üóëÔ∏è
                  </button>
                )}
              </div>

              <div className="post-content">
                <p>{item.body}</p>
                {item.photo && <img src={item.photo} alt="" />}
              </div>

              <div className="post-actions">
                {item.likes?.includes(state?._id) ? (
                  <button onClick={() => unlikePost(item._id)}>
                    ‚ù§Ô∏è Unlike ({item.likes.length})
                  </button>
                ) : (
                  <button onClick={() => likePost(item._id)}>
                    ü§ç Like ({item.likes?.length || 0})
                  </button>
                )}
              </div>

              <div className="comments-section">
                <h6>Comments</h6>
                {item.comments?.map((comment) => (
                  <div key={comment._id} className="comment-item">
                    <div className="comment-pic">
                      <img src={comment.postedBy?.Photo || "https://cdn-icons-png.flaticon.com/128/3177/3177440.png"} alt="" />
                    </div>
                    <div className="comment-content">
                      <h6>{comment.postedBy?.name}</h6>
                      <p>{comment.comment}</p>
                    </div>
                  </div>
                ))}
              </div>

              <div className="add-comment">
                <input
                  type="text"
                  placeholder="Add a comment..."
                  onKeyPress={(e) => {
                    if (e.key === 'Enter' && e.target.value.trim()) {
                      makeComment(e.target.value, item._id);
                      e.target.value = '';
                    }
                  }}
                />
              </div>
            </div>
          ))
        ) : (
          <div className="no-posts">
            <p>No posts from people you follow yet.</p>
            <p>Follow some users to see their posts here!</p>
          </div>
        )}
      </div>
    </div>
  );
}
