import React, { useState, useEffect } from "react";
import "./Createpost.css";
import { toast } from 'react-toastify';
import { useNavigate } from "react-router-dom";
import config from "../config/config";

export default function Createpost() {
  const [body, setBody] = useState("");
  const [image, setImage] = useState(null);
  const [url, setUrl] = useState("");
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  // Toast functions
  const notifyA = (msg) => toast.error(msg);
  const notifyB = (msg) => toast.success(msg);

  useEffect(() => {
    fetchUser();
  }, []);

  const fetchUser = async () => {
    try {
      const userData = JSON.parse(localStorage.getItem("user"));
      const token = localStorage.getItem("jwt");

      const response = await fetch(`${config.BACKEND_URL}/user/${userData._id}`, {
        headers: {
          Authorization: "Bearer " + token,
        },
      });

      if (!response.ok) {
        throw new Error('Failed to fetch user data');
      }

      const data = await response.json();
      setUser(data);
    } catch (error) {
      console.error("Error fetching user:", error);
      toast.error("Failed to load user data");
    }
  };

  useEffect(() => {
    // saving post to mongodb
    if (url) {
      fetch(`${config.BACKEND_URL}/createPost`, {
        method: "post",
        headers: {
          "Content-Type": "application/json",
          "Authorization": "Bearer " + localStorage.getItem("jwt")
        },
        body: JSON.stringify({
          body,
          photo: url
        })
      }).then(res => res.json())
        .then(data => {
          if (data.error) {
            notifyA(data.error);
          } else {
            notifyB("Successfully Posted");
            navigate("/");
          }
        })
        .catch(err => console.log(err));
    }
  }, [url]);

  // posting image to cloudinary
  const postDetails = () => {
    const data = new FormData();
    if (image) {
      data.append("file", image);
      data.append("upload_preset", "insta-clone");
      data.append("cloud_name", "dchbf0ovs");

      fetch("https://api.cloudinary.com/v1_1/dchbf0ovs/image/upload", {
        method: "post",
        body: data,
      })
        .then((res) => res.json())
        .then((data) => {
          setUrl(data.url);
          fetch(`${config.BACKEND_URL}/createPost`, {
            method: "post",
            headers: {
              "Content-Type": "application/json",
              Authorization: "Bearer " + localStorage.getItem("jwt"),
            },
            body: JSON.stringify({
              body,
              photo: data.url,
            }),
          })
            .then((res) => res.json())
            .then((data) => {
              console.log(data);
              toast.success("Post created successfully!");
              setBody("");
              setImage(null);
              setUrl("");
            })
            .catch((err) => {
              console.error("Error creating post:", err);
              toast.error("Failed to create post");
            });
        })
        .catch((err) => {
          console.error("Error uploading image:", err);
          toast.error("Failed to upload image");
        });
    } else {
      toast.error("Please select an image");
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
  };

  const loadfile = (event) => {
    var output = document.getElementById("output");
    output.src = URL.createObjectURL(event.target.files[0]);
    output.onload = function () {
      URL.revokeObjectURL(output.src); // free memory
    };
  };

  return (
    <div className="createPost">
      {/* //header */}
      <div className="post-header">
        <h4 style={{ margin: "3px auto" }}>Create New Post</h4>
        <button id="post-btn" onClick={postDetails}>
          Share
        </button>
      </div>
      {/* image preview */}
      <div className="main-div">
        <img
          id="output"
          src={user?.Photo || "https://cdn-icons-png.flaticon.com/128/3177/3177440.png"}
          alt={user?.name}
          onError={(e) => {
            console.log("Error loading profile picture. User data:", user);
            console.log("Attempted photo URL:", user?.Photo);
            console.log("Error details:", e);
            e.target.src = "https://cdn-icons-png.flaticon.com/128/3177/3177440.png";
          }}
          style={{ width: "40px", height: "40px", borderRadius: "50%", objectFit: "cover" }}
        />
        <div className="post-details">
          <textarea
            value={body}
            onChange={(e) => setBody(e.target.value)}
            placeholder="What's on your mind?"
            rows="3"
          />
        </div>
      </div>
      {/* details */}
      <div className="post-actions">
        <div className="file-input">
          <label htmlFor="file-upload" className="file-label">
            ðŸ“· Add Photo
          </label>
          <input
            id="file-upload"
            type="file"
            accept="image/*"
            onChange={handleFileChange}
            style={{ display: "none" }}
          />
        </div>
        {image && (
          <div className="selected-file">
            <p>Selected: {image.name}</p>
          </div>
        )}
      </div>
    </div>
  );
}
