import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Audiobook.css";
import { toast } from "react-toastify";

export default function Audiobook() {
  const navigate = useNavigate();
  const [audiobooks, setAudiobooks] = useState([]);
  const [loading, setLoading] = useState(true);

  // Toast function for errors
  const notifyError = (msg) => toast.error(msg);

  // Function to get audio duration
  const getAudioDuration = async (audioUrl) => {
    try {
      return new Promise((resolve) => {
        const audio = new Audio();
        audio.addEventListener('loadedmetadata', () => {
          resolve(Math.round(audio.duration));
        });
        audio.addEventListener('error', () => {
          console.error('Error loading audio:', audioUrl);
          resolve(0);
        });
        audio.src = audioUrl;
      });
    } catch (error) {
      console.error('Error getting audio duration:', error);
      return 0;
    }
  };

  useEffect(() => {
    const fetchAudiobooks = async () => {
      try {
        const response = await fetch("http://localhost:4000/audiobooks");
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const result = await response.json();
        console.log("Frontend received data:", result[0]); // Log first audiobook received

        if (!Array.isArray(result)) {
          throw new Error("Expected array of audiobooks");
        }

        if (result.length === 0) {
          console.log("No audiobooks found in database");
          setAudiobooks([]);
          setLoading(false);
          return;
        }

        // Verify the first audiobook has required fields
        const firstBook = result[0];
        console.log("First audiobook fields:", {
          hasName: !!firstBook.name,
          name: firstBook.name,
          hasAuthor: !!firstBook.author,
          author: firstBook.author
        });

        // Get durations for all audiobooks
        const audiobooksWithDuration = await Promise.all(
          result.map(async (book) => {
            console.log("Processing book:", book); // Debug log for each book
            const duration = await getAudioDuration(book.audioUrl);
            return {
              ...book,
              duration: duration || 0
            };
          })
        );

        console.log("Processed audiobooks with duration:", audiobooksWithDuration); // Debug log
        setAudiobooks(audiobooksWithDuration);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching audiobooks:", err);
        notifyError("Failed to fetch audiobooks");
        setLoading(false);
      }
    };

    fetchAudiobooks();
  }, []);

  const handleCardClick = (audiobookId) => {
    navigate(`/audiobook/${audiobookId}`);
  };

  // Format duration from seconds to HH:MM:SS or MM:SS
  const formatDuration = (seconds) => {
    if (!seconds || isNaN(seconds)) return "00:00";

    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const remainingSeconds = seconds % 60;

    if (hours > 0) {
      return `${hours}:${minutes.toString().padStart(2, '0')}:${remainingSeconds.toString().padStart(2, '0')}`;
    }
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  if (loading) {
    return <div className="loading">Loading audiobooks...</div>;
  }

  if (audiobooks.length === 0) {
    return <div className="loading">No audiobooks found</div>;
  }

  return (
    <div className="audiobook-container">
      <h1 className="audiobook-title">Audiobooks</h1>
      <div className="audiobook-grid">
        {audiobooks.map((book) => {
          // Log each book being rendered
          console.log("Rendering book:", {
            id: book._id,
            title: book.title,
            author: book.author
          });

          return (
            <div
              key={book._id}
              className="audiobook-card"
              onClick={() => handleCardClick(book._id)}
            >
              <div className="audiobook-thumbnail">
                <img 
                  src={book.thumbnailUrl} 
                  alt={book.title || "Audiobook cover"} 
                  onError={(e) => {
                    e.target.src = "https://via.placeholder.com/300x400?text=No+Image";
                  }}
                />
              </div>
              <div className="audiobook-details">
                <h3 className="audiobook-name">
                  {book.name || "No Title Available"}
                </h3>
                <p className="audiobook-author">
                  By {book.author || "Unknown Author"}
                </p>
                {book.description && (
                  <p className="audiobook-description">
                    {book.description}
                  </p>
                )}
                <div className="audiobook-meta">
                  <span className="audiobook-duration">
                    {book.duration ? `${Math.floor(book.duration / 60)}:${(book.duration % 60).toString().padStart(2, '0')}` : "00:00"}
                  </span>
                </div>
                <div className="audiobook-rating">
                  {[...Array(5)].map((_, index) => (
                    <span
                      key={index}
                      className={`star ${index < Math.floor(book.rating || 0) ? 'filled' : ''}`}
                    >
                      â˜…
                    </span>
                  ))}
                  <span className="rating-value">({(book.rating || 0).toFixed(1)})</span>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
