import React, { useEffect, useState } from "react";
import PostDetail from "./PostDetail";
import "./Profile.css";
import ProfilePic from "./ProfilePic";
import VideoCard from "./VideoCard";

export default function Profie() {
  var picLink = "https://cdn-icons-png.flaticon.com/128/3177/3177440.png"
  const [pic, setPic] = useState([]);
  const [show, setShow] = useState(false)
  const [posts, setPosts] = useState([]);
  const [user, setUser] = useState("")
  const [changePic, setChangePic] = useState(false)
  const [videos, setVideos] = useState([]);
  const [activeTab, setActiveTab] = useState('posts'); // 'posts' or 'videos'

  const toggleDetails = (posts) => {
    if (show) {
      setShow(false);
    } else {
      setShow(true);
      setPosts(posts);
    }
  };

  const changeprofile = () => {
    if (changePic) {
      setChangePic(false)
    } else {
      setChangePic(true)
    }
  }

  useEffect(() => {
    const userId = JSON.parse(localStorage.getItem("user"))._id;
    console.log('Fetching data for user:', userId);

    // Fetch user data and posts
    fetch(`http://localhost:4000/user/${userId}`, {
      headers: {
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
    })
      .then((res) => res.json())
      .then((result) => {
        console.log('User data response:', result);
        setPic(result.post);
        setUser(result.user);
      })
      .catch(err => {
        console.error('Error fetching user data:', err);
      });

    // Fetch user's videos
    console.log('Fetching videos for user:', userId);
    fetch(`http://localhost:4000/video/user/${userId}`, {
      headers: {
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
    })
      .then((res) => {
        console.log('Videos response status:', res.status);
        return res.json();
      })
      .then((result) => {
        console.log('Videos response data:', result);
        setVideos(result);
      })
      .catch(err => {
        console.error('Error fetching videos:', err);
        setVideos([]);
      });
  }, []);

  const handleVideoUpdate = () => {
    const userId = JSON.parse(localStorage.getItem("user"))._id;
    console.log('Updating videos for user:', userId);
    // Fetch user's videos again
    fetch(`http://localhost:4000/video/user/${userId}`, {
      headers: {
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
    })
      .then((res) => {
        console.log('Video update response status:', res.status);
        return res.json();
      })
      .then((result) => {
        console.log('Video update response data:', result);
        setVideos(result);
      })
      .catch(err => {
        console.error('Error updating videos:', err);
        setVideos([]);
      });
  };

  return (
    <div className="profile">
      {/* Profile frame */}
      <div className="profile-frame">
        {/* profile-pic */}
        <div className="profile-pic">
          <img
            onClick={changeprofile}
            src={user.Photo ? user.Photo : picLink}
            alt=""
          />
        </div>
        {/* profile-data */}
        <div className="pofile-data">
          <h1>{JSON.parse(localStorage.getItem("user")).name}</h1>
          <div className="profile-info" style={{ display: "flex" }}>
            <p>{pic ? pic.length : "0"} posts</p>
            <p>{videos ? videos.length : "0"} videos</p>
            <p>{user.followers ? user.followers.length : "0"} followers</p>
            <p>{user.following ? user.following.length : "0"} following</p>
          </div>
        </div>
      </div>
      <hr
        style={{
          width: "90%",
          opacity: "0.8",
          margin: "25px auto",
        }}
      />

      {/* Tabs */}
      <div className="profile-tabs">
        <button
          className={`tab-button ${activeTab === 'posts' ? 'active' : ''}`}
          onClick={() => setActiveTab('posts')}
        >
          Posts
        </button>
        <button
          className={`tab-button ${activeTab === 'videos' ? 'active' : ''}`}
          onClick={() => setActiveTab('videos')}
        >
          Videos
        </button>
      </div>

      {/* Content based on active tab */}
      {activeTab === 'posts' ? (
      <div className="gallery">
        {pic.map((pics) => {
          return <img key={pics._id} src={pics.photo}
            onClick={() => {
              toggleDetails(pics)
            }}
            className="item"></img>;
        })}
      </div>
      ) : (
        <div className="videos-grid">
          {videos && videos.map((video) => (
            <VideoCard
              key={video._id}
              video={video}
              onVideoUpdate={handleVideoUpdate}
            />
          ))}
        </div>
      )}

      {show &&
        <PostDetail item={posts} toggleDetails={toggleDetails} />
      }
      {
        changePic &&
        <ProfilePic changeprofile={changeprofile} />
      }
    </div>
  );
}
