import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import { UserContext } from '../App';
import './ReelCard.css';

const ReelCard = ({ reel, onReelUpdate }) => {
  const { state } = useContext(UserContext);
  const [showOptions, setShowOptions] = useState(false);

  const formatDuration = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = Math.floor(seconds % 60);
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const formatViews = (views) => {
    if (views >= 1000000) {
      return `${(views / 1000000).toFixed(1)}M views`;
    } else if (views >= 1000) {
      return `${(views / 1000).toFixed(1)}K views`;
    }
    return `${views} views`;
  };

  const formatDate = (date) => {
    const now = new Date();
    const uploadDate = new Date(date);
    const diffInSeconds = Math.floor((now - uploadDate) / 1000);

    if (diffInSeconds < 60) {
      return 'Just now';
    } else if (diffInSeconds < 3600) {
      const minutes = Math.floor(diffInSeconds / 60);
      return `${minutes} minute${minutes > 1 ? 's' : ''} ago`;
    } else if (diffInSeconds < 86400) {
      const hours = Math.floor(diffInSeconds / 3600);
      return `${hours} hour${hours > 1 ? 's' : ''} ago`;
    } else if (diffInSeconds < 604800) {
      const days = Math.floor(diffInSeconds / 86400);
      return `${days} day${days > 1 ? 's' : ''} ago`;
    } else {
      return uploadDate.toLocaleDateString();
    }
  };

  const handleLike = async () => {
    try {
      const response = await fetch(`http://localhost:4000/reel/like/${reel._id}`, {
        method: 'put',
        headers: {
          Authorization: `Bearer ${localStorage.getItem('jwt')}`
        }
      });

      if (response.ok) {
        onReelUpdate();
      }
    } catch (error) {
      console.error('Error liking reel:', error);
    }
  };

  const handleDelete = async () => {
    try {
      const response = await fetch(`http://localhost:4000/reel/delete/${reel._id}`, {
        method: 'delete',
        headers: {
          Authorization: `Bearer ${localStorage.getItem('jwt')}`
        }
      });

      if (response.ok) {
        onReelUpdate();
      }
    } catch (error) {
      console.error('Error deleting reel:', error);
    }
  };

  return (
    <div className="reel-card">
      <Link to={`/reel/${reel._id}`} className="reel-thumbnail">
        <img src={reel.thumbnailUrl} alt={reel.title} />
        <div className="reel-duration">
          {formatDuration(reel.duration)}
        </div>
      </Link>

      <div className="reel-info">
        <div className="reel-header">
          <Link to={`/profile/${reel.postedBy._id}`} className="channel-avatar">
            <img 
              src={reel.postedBy.Photo || "https://cdn-icons-png.flaticon.com/128/3177/3177440.png"} 
              alt={reel.postedBy.name}
            />
          </Link>
          <div className="reel-details">
            <Link to={`/reel/${reel._id}`} className="reel-title">
              {reel.title}
            </Link>
            <Link to={`/profile/${reel.postedBy._id}`} className="channel-name">
              {reel.postedBy.name}
            </Link>
            <div className="reel-stats">
              <span className="views">{formatViews(reel.views)}</span>
              <span className="dot">â€¢</span>
              <span className="upload-time">{formatDate(reel.createdAt)}</span>
            </div>
          </div>
        </div>

        <div className="reel-actions">
          <button
            className={`like-button ${reel.likes.includes(state?._id) ? 'liked' : ''}`}
            onClick={handleLike}
          >
            <i className="material-icons">thumb_up</i>
            <span>{reel.likes.length}</span>
          </button>

          {state && reel.postedBy._id === state._id && (
            <div className="reel-options">
              <button
                className="options-button"
                onClick={() => setShowOptions(!showOptions)}
              >
                <i className="material-icons">more_vert</i>
              </button>
              {showOptions && (
                <div className="options-menu">
                  <button onClick={handleDelete}>
                    <i className="material-icons">delete</i>
                    Delete
                  </button>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ReelCard;
