import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../App';
import VideoCard from '../components/VideoCard';
import VideoUploadModal from '../components/VideoUploadModal';
import './Videos.css';

const Videos = () => {
  const { state } = useContext(UserContext);
  const [videos, setVideos] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('newest');
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [activeTab, setActiveTab] = useState('all'); // 'all' or 'following'

  useEffect(() => {
    fetchVideos();
  }, [searchQuery, sortBy, activeTab]);

  const fetchVideos = async () => {
    try {
      const endpoint = activeTab === 'following' ? '/followingvideos' : '/allvideos';
      const queryString = new URLSearchParams({
        q: searchQuery,
        sort: sortBy
      }).toString();

      const response = await fetch(`http://localhost:4000${endpoint}?${queryString}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('jwt')}`
        }
      });

      if (!response.ok) {
        throw new Error('Failed to fetch videos');
      }

      const data = await response.json();
      setVideos(data);
    } catch (error) {
      console.error('Error fetching videos:', error);
    }
  };

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSort = (e) => {
    setSortBy(e.target.value);
  };

  const handleUploadSuccess = () => {
    setShowUploadModal(false);
    fetchVideos();
  };

  return (
    <div className="videos-container">
      <div className="videos-header">
        <div className="search-sort-container">
          <input
            type="text"
            placeholder="Search videos..."
            value={searchQuery}
            onChange={handleSearch}
            className="search-input"
          />
          <select value={sortBy} onChange={handleSort} className="sort-select">
            <option value="newest">Newest</option>
            <option value="oldest">Oldest</option>
            <option value="mostViewed">Most Viewed</option>
            <option value="mostLiked">Most Liked</option>
          </select>
        </div>

        {state && (
          <button
            className="upload-button"
            onClick={() => setShowUploadModal(true)}
          >
            <i className="material-icons">add</i>
            Upload Video
          </button>
        )}
      </div>

      <div className="videos-tabs">
        <button
          className={`tab-button ${activeTab === 'all' ? 'active' : ''}`}
          onClick={() => setActiveTab('all')}
        >
          All Videos
        </button>
        {state && (
          <button
            className={`tab-button ${activeTab === 'following' ? 'active' : ''}`}
            onClick={() => setActiveTab('following')}
          >
            Following
          </button>
        )}
      </div>

      <div className="videos-grid">
        {videos.map((video) => (
          <VideoCard
            key={video._id}
            video={video}
            onVideoUpdate={fetchVideos}
          />
        ))}
      </div>

      {showUploadModal && (
        <VideoUploadModal
          onClose={() => setShowUploadModal(false)}
          onUploadSuccess={handleUploadSuccess}
        />
      )}
    </div>
  );
};

export default Videos;
