import React, { useState, useEffect } from 'react';
import './VideoPage.css';
import { useNavigate } from 'react-router-dom';

const VideoPage = () => {
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [showUpload, setShowUpload] = useState(false);
  const [uploadData, setUploadData] = useState({
    title: '',
    description: '',
    tags: '',
    file: null
  });
  const [uploadProgress, setUploadProgress] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    fetchVideos();
  }, []);

  useEffect(() => {
    if (searchQuery) {
      const delayDebounceFn = setTimeout(() => {
        searchVideos();
      }, 500);
      return () => clearTimeout(delayDebounceFn);
    } else {
      fetchVideos();
    }
  }, [searchQuery]);

  const fetchVideos = async () => {
    try {
      const response = await fetch('http://localhost:4000/api/videos');
      const data = await response.json();
      setVideos(data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching videos:', error);
      setLoading(false);
    }
  };

  const searchVideos = async () => {
    try {
      const response = await fetch(`http://localhost:4000/api/videos/search?query=${searchQuery}`);
      const data = await response.json();
      setVideos(data);
    } catch (error) {
      console.error('Error searching videos:', error);
    }
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('title', uploadData.title);
    formData.append('description', uploadData.description);
    formData.append('tags', uploadData.tags);
    formData.append('video', uploadData.file);

    try {
      const response = await fetch('http://localhost:4000/api/videos/upload', {
        method: 'POST',
        body: formData,
        onUploadProgress: (progressEvent) => {
          const progress = (progressEvent.loaded / progressEvent.total) * 100;
          setUploadProgress(Math.round(progress));
        }
      });
      const data = await response.json();
      if (data._id) {
        setShowUpload(false);
        setUploadData({ title: '', description: '', tags: '', file: null });
        setUploadProgress(0);
        fetchVideos();
      }
    } catch (error) {
      console.error('Error uploading video:', error);
    }
  };

  const handleVideoClick = (videoId) => {
    navigate(`/video/${videoId}`);
  };

  const formatViews = (views) => {
    if (views >= 1000000) {
      return `${(views / 1000000).toFixed(1)}M`;
    } else if (views >= 1000) {
      return `${(views / 1000).toFixed(1)}K`;
    }
    return views;
  };

  return (
    <div className="video-page">
      <div className="video-header">
        <div className="search-bar">
          <i className="material-icons">search</i>
          <input
            type="text"
            placeholder="Search videos..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        <button className="upload-btn" onClick={() => setShowUpload(true)}>
          <i className="material-icons">cloud_upload</i>
          <span>Upload</span>
        </button>
      </div>

      {showUpload && (
        <div className="upload-modal">
          <div className="upload-content">
            <div className="upload-header">
              <h2>Upload Video</h2>
              <button className="close-btn" onClick={() => setShowUpload(false)}>
                <i className="material-icons">close</i>
              </button>
            </div>
            <form onSubmit={handleUpload}>
              <div className="upload-form-group">
                <label>Title</label>
                <input
                  type="text"
                  placeholder="Enter video title"
                  value={uploadData.title}
                  onChange={(e) => setUploadData({...uploadData, title: e.target.value})}
                  required
                />
              </div>
              <div className="upload-form-group">
                <label>Description</label>
                <textarea
                  placeholder="Enter video description"
                  value={uploadData.description}
                  onChange={(e) => setUploadData({...uploadData, description: e.target.value})}
                  required
                />
              </div>
              <div className="upload-form-group">
                <label>Tags (comma separated)</label>
                <input
                  type="text"
                  placeholder="Enter tags (e.g., music, gaming, tutorial)"
                  value={uploadData.tags}
                  onChange={(e) => setUploadData({...uploadData, tags: e.target.value})}
                />
              </div>
              <div className="upload-form-group">
                <label>Video File</label>
                <div className="file-upload">
                  <input
                    type="file"
                    accept="video/*"
                    onChange={(e) => setUploadData({...uploadData, file: e.target.files[0]})}
                    required
                  />
                  <span className="file-name">
                    {uploadData.file ? uploadData.file.name : 'Choose a video file'}
                  </span>
                </div>
              </div>
              {uploadProgress > 0 && (
                <div className="upload-progress">
                  <div className="progress-bar" style={{ width: `${uploadProgress}%` }}></div>
                  <span>{uploadProgress}%</span>
                </div>
              )}
              <div className="upload-actions">
                <button type="submit" className="upload-submit-btn">
                  <i className="material-icons">cloud_upload</i>
                  Upload
                </button>
                <button type="button" className="cancel-btn" onClick={() => setShowUpload(false)}>
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {loading ? (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading videos...</p>
        </div>
      ) : (
        <div className="videos-grid">
          {videos.map((video) => (
            <div key={video._id} className="video-card" onClick={() => handleVideoClick(video._id)}>
              <div className="video-thumbnail">
                <img src={video.thumbnail} alt={video.title} />
                <span className="video-duration">{video.duration}</span>
              </div>
              <div className="video-info">
                <div className="video-channel">
                  <img src={video.postedBy.Photo} alt={video.postedBy.name} className="channel-avatar" />
                  <div className="video-details">
                    <h3>{video.title}</h3>
                    <p className="channel-name">{video.postedBy.name}</p>
                    <div className="video-stats">
                      <span>{formatViews(video.views)} views</span>
                      <span>â€¢</span>
                      <span>{video.likes.length} likes</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default VideoPage;
