import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './UploadReel.css';

const UploadReel = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState('');
  const [video, setVideo] = useState(null);
  const [preview, setPreview] = useState(null);
  const [uploading, setUploading] = useState(false);
  const navigate = useNavigate();

  const categories = [
    { id: 'education', name: 'Education' },
    { id: 'business', name: 'Business' },
    { id: 'entertainment', name: 'Entertainment' },
    { id: 'sports-fitness', name: 'Sports & Fitness' },
    { id: 'gaming', name: 'Gaming' },
    { id: 'food-travel', name: 'Food & Travel' }
  ];

  const handleVideoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setVideo(file);
      const videoUrl = URL.createObjectURL(file);
      setPreview(videoUrl);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!video || !title || !description || !category) {
      alert('Please fill in all fields and select a video');
      return;
    }

    setUploading(true);
    const formData = new FormData();
    formData.append('video', video);
    formData.append('title', title);
    formData.append('description', description);
    formData.append('category', category);

    try {
      await axios.post('/api/reels/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      navigate('/reels');
    } catch (error) {
      console.error('Error uploading reel:', error);
      alert('Error uploading reel. Please try again.');
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="upload-reel-container">
      <h2>Upload New Reel</h2>
      <form onSubmit={handleSubmit} className="upload-form">
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter reel title"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Enter reel description"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="category">Category</label>
          <select
            id="category"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            required
          >
            <option value="">Select a category</option>
            {categories.map(cat => (
              <option key={cat.id} value={cat.id}>
                {cat.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="video">Video</label>
          <input
            type="file"
            id="video"
            accept="video/*"
            onChange={handleVideoChange}
            required
          />
        </div>

        {preview && (
          <div className="video-preview">
            <video src={preview} controls />
          </div>
        )}

        <button
          type="submit"
          className="upload-btn"
          disabled={uploading}
        >
          {uploading ? 'Uploading...' : 'Upload Reel'}
        </button>
      </form>
    </div>
  );
};

export default UploadReel;
