import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import "./bottombar.css";
import { GestureWheel } from '../components/GestureWheel';
import { cn } from '@/lib/utils';

export default function BottomNavbar() {
  const location = useLocation();
  const [showWheel, setShowWheel] = useState(false);
  const isActive = (path) => location.pathname === path;

  const handleReelsClick = (e) => {
    e.preventDefault();
    setShowWheel(!showWheel);
  };

  return (
    <>
      <div className="bottom-navbar">
        <Link to="/" className={`bottom-nav-item ${isActive("/") ? "active" : ""}`}>
          <i className="material-icons">home</i>
          <span>Home</span>
        </Link>
        <div 
          onClick={handleReelsClick}
          className={`bottom-nav-item ${isActive("/reels") ? "active" : ""}`}
        >
          <i className="material-icons">movie</i>
          <span>Reels</span>
        </div>
        <Link to="/videos" className={`bottom-nav-item ${isActive("/videos") ? "active" : ""}`}>
          <i className="material-icons">videocam</i>
          <span>Long Videos</span>
        </Link>
        <Link to="/profile" className={`bottom-nav-item ${isActive("/profile") ? "active" : ""}`}>
          <i className="material-icons">person</i>
          <span>Profile</span>
        </Link>
      </div>

      {showWheel && (
        <div className="gesture-wheel-overlay">
          <GestureWheel />
        </div>
      )}
    </>
  );
}
