import React, { useContext } from "react";
import logo from "../img/logo.png";
import "./Navbar.css";
import { Link } from "react-router-dom";
import { LoginContext } from "../context/LoginContext";

export default function Navbar({ login }) {
  const { setModalOpen } = useContext(LoginContext);

  const topNavItems = () => {
    const token = localStorage.getItem("jwt");
    if (login || token) {
      return [
        <>
          <Link to="/createPost">Create Post</Link>
          <Link style={{ marginLeft: "20px" }} to="/followingpost">
            My Following
          </Link>
          <Link to={""}>
            <button className="primaryBtn" onClick={() => setModalOpen(true)}>
              Log Out
            </button>
          </Link>
        </>,
      ];
    } else {
      return [
        <>
          <Link to="/signup">
            <li>Sign Up</li>
          </Link>
          <Link to="/signin">
            <li>Sign In</li>
          </Link>
        </>,
      ];
    }
  };

  const showBottomNav = () => {
    const token = localStorage.getItem("jwt");
    return login || token;
  };

  return (
    <>
      <div className="navbar">
        <h2 className="logo">Vybes</h2>
        <ul className="nav-menu">{topNavItems()}</ul>
      </div>

      {showBottomNav() && (
        <div className="bottom-navbar">
          <Link to="/" className="bottom-nav-item">
            <i className="material-icons">home</i>
            <span>Home</span>
          </Link>
          <Link to="/reels" className="bottom-nav-item">
            <i className="material-icons">movie</i>
            <span>Reels</span>
          </Link>
          <Link to="/videos" className="bottom-nav-item">
            <i className="material-icons">videocam</i>
            <span>Long Videos</span>
          </Link>
          <Link to="/profile" className="bottom-nav-item">
            <i className="material-icons">person</i>
            <span>Profile</span>
          </Link>
        </div>
      )}
    </>
  );
}
