import React, { useState, useEffect, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { UserContext } from '../context/UserContext';
import './VideoCard.css';
import { toast } from 'react-toastify';
import config from '../config/config';

const VideoCard = ({ video, onVideoUpdate }) => {
  const { state } = useContext(UserContext);
  const navigate = useNavigate();
  const [showOptions, setShowOptions] = useState(false);
  const [isLiked, setIsLiked] = useState(false);
  const [likeCount, setLikeCount] = useState(0);
  const [showDeleteModal, setShowDeleteModal] = useState(false);

  useEffect(() => {
    if (video.likes && state) {
      setIsLiked(video.likes.includes(state._id));
      setLikeCount(video.likes.length);
    }
  }, [video.likes, state]);

  const formatViews = (views) => {
    if (views >= 1000000) {
      return `${(views / 1000000).toFixed(1)}M views`;
    } else if (views >= 1000) {
      return `${(views / 1000).toFixed(1)}K views`;
    }
    return `${views} views`;
  };

  const formatDate = (date) => {
    const now = new Date();
    const videoDate = new Date(date);
    const diffTime = Math.abs(now - videoDate);
    const diffDays = Math.floor(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 0) return 'Today';
    if (diffDays === 1) return 'Yesterday';
    if (diffDays < 7) return `${diffDays} days ago`;
    if (diffDays < 30) {
      const weeks = Math.floor(diffDays / 7);
      return `${weeks} ${weeks === 1 ? 'week' : 'weeks'} ago`;
    }
    return videoDate.toLocaleDateString();
  };

  const formatDuration = (seconds) => {
    if (!seconds) return '0:00';
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = Math.floor(seconds % 60);
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const handleLike = async () => {
    if (!state) {
      toast.error('Please log in to like videos');
      return;
    }

    try {
      const response = await fetch(`${config.BACKEND_URL}/like`, {
        method: 'put',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('jwt')}`
        },
        body: JSON.stringify({
          videoId: video._id
        })
      });

      if (!response.ok) {
        throw new Error('Failed to like video');
      }

      const data = await response.json();
      setIsLiked(!isLiked);
      setLikeCount(isLiked ? likeCount - 1 : likeCount + 1);

      if (onVideoUpdate) {
        onVideoUpdate();
      }
    } catch (error) {
      console.error('Error liking video:', error);
      toast.error('Failed to like video');
    }
  };

  const handleDelete = async () => {
    try {
      const response = await fetch(`${config.BACKEND_URL}/delete/${video._id}`, {
        method: 'delete',
        headers: {
          Authorization: `Bearer ${localStorage.getItem('jwt')}`
        }
      });

      if (!response.ok) {
        throw new Error('Failed to delete video');
      }

      toast.success('Video deleted successfully');
      if (onVideoUpdate) {
        onVideoUpdate();
      }
    } catch (error) {
      console.error('Error deleting video:', error);
      toast.error('Failed to delete video');
    } finally {
      setShowDeleteModal(false);
    }
  };

  return (
    <div className="video-card">
      <Link to={`/video/${video._id}`} className="video-thumbnail">
        <img src={video.thumbnailUrl} alt={video.title} />
        <div className="video-duration">
          {formatDuration(video.duration)}
        </div>
      </Link>

      <div className="video-info">
        <div className="video-header">
          <Link to={`/profile/${video.postedBy._id}`} className="channel-avatar">
            <img 
              src={video.postedBy.Photo || "https://cdn-icons-png.flaticon.com/128/3177/3177440.png"} 
              alt={video.postedBy.name}
            />
          </Link>
          <div className="video-details">
        <Link to={`/video/${video._id}`} className="video-title">
          {video.title}
        </Link>
          <Link to={`/profile/${video.postedBy._id}`} className="channel-name">
            {video.postedBy.name}
          </Link>
          <div className="video-stats">
              <span className="views">{formatViews(video.views)}</span>
            <span className="dot">‚Ä¢</span>
              <span className="upload-time">{formatDate(video.createdAt)}</span>
            </div>
          </div>
        </div>

        <div className="video-actions">
          <button
            className={`like-button ${isLiked ? 'liked' : ''}`}
            onClick={handleLike}
          >
            {isLiked ? '‚ù§Ô∏è' : 'ü§ç'} {likeCount}
          </button>

          {state && video.postedBy._id === state._id && (
            <div className="video-options">
              <button
                className="options-button"
                onClick={() => setShowOptions(!showOptions)}
              >
                <i className="material-icons">more_vert</i>
              </button>
              {showOptions && (
                <div className="options-menu">
                  <button onClick={() => setShowDeleteModal(true)}>
                    <i className="material-icons">delete</i>
                    Delete
                  </button>
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      {showDeleteModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Delete Video</h3>
            <p>Are you sure you want to delete this video? This action cannot be undone.</p>
            <div className="modal-actions">
              <button onClick={handleDelete} className="delete-confirm">
                Delete
              </button>
              <button onClick={() => setShowDeleteModal(false)} className="cancel">
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default VideoCard;
