import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaHeart, FaComment, FaShare, FaPlay, FaPause } from 'react-icons/fa';
import './Reels.css';

const Reels = () => {
  const [reels, setReels] = useState([]);
  const [currentReelIndex, setCurrentReelIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const navigate = useNavigate();

  const categories = [
    { id: 'all', name: 'All' },
    { id: 'education', name: 'Education' },
    { id: 'business', name: 'Business' },
    { id: 'entertainment', name: 'Entertainment' },
    { id: 'sports-fitness', name: 'Sports & Fitness' },
    { id: 'gaming', name: 'Gaming' },
    { id: 'food-travel', name: 'Food & Travel' }
  ];

  useEffect(() => {
    fetchReels();
  }, [selectedCategory]);

  const fetchReels = async () => {
    try {
      const endpoint = selectedCategory === 'all' 
        ? '/api/reels'
        : `/api/reels/category/${selectedCategory}`;

      const response = await axios.get(endpoint);
      setReels(response.data);
    } catch (error) {
      console.error('Error fetching reels:', error);
    }
  };

  const handleLike = async (reelId) => {
    try {
      await axios.put(`/api/reels/like/${reelId}`);
      fetchReels();
    } catch (error) {
      console.error('Error liking reel:', error);
    }
  };

  const handleComment = async (reelId, comment) => {
    try {
      await axios.put(`/api/reels/comment/${reelId}`, { text: comment });
      fetchReels();
    } catch (error) {
      console.error('Error commenting on reel:', error);
    }
  };

  const handleShare = (reelId) => {
    const shareUrl = `${window.location.origin}/reels/${reelId}`;
    navigator.clipboard.writeText(shareUrl);
    alert('Link copied to clipboard!');
  };

  const handleVideoClick = () => {
    setIsPlaying(!isPlaying);
  };

  const handleScroll = (direction) => {
    if (direction === 'up' && currentReelIndex > 0) {
      setCurrentReelIndex(currentReelIndex - 1);
    } else if (direction === 'down' && currentReelIndex < reels.length - 1) {
      setCurrentReelIndex(currentReelIndex + 1);
    }
  };

  const currentReel = reels[currentReelIndex];

  return (
    <div className="reels-container">
      <div className="categories-bar">
        {categories.map(category => (
          <button
            key={category.id}
            className={`category-btn ${selectedCategory === category.id ? 'active' : ''}`}
            onClick={() => setSelectedCategory(category.id)}
          >
            {category.name}
          </button>
        ))}
      </div>

      <div className="reels-feed">
        {currentReel && (
          <div className="reel-item">
            <video
              src={currentReel.videoUrl}
              loop
              playsInline
              autoPlay={isPlaying}
              onClick={handleVideoClick}
              className="reel-video"
            />

            <div className="reel-info">
              <h3>{currentReel.title}</h3>
              <p>{currentReel.description}</p>
              <p className="reel-author">Posted by: {currentReel.postedBy?.name}</p>
            </div>

            <div className="reel-actions">
              <button onClick={() => handleLike(currentReel._id)}>
                <FaHeart className={currentReel.likes?.includes(currentReel._id) ? 'liked' : ''} />
                <span>{currentReel.likes?.length || 0}</span>
              </button>
              <button onClick={() => handleComment(currentReel._id)}>
                <FaComment />
                <span>{currentReel.comments?.length || 0}</span>
              </button>
              <button onClick={() => handleShare(currentReel._id)}>
                <FaShare />
              </button>
            </div>

            <div className="reel-navigation">
              <button
                className="nav-btn"
                onClick={() => handleScroll('up')}
                disabled={currentReelIndex === 0}
              >
                ↑
              </button>
              <button
                className="nav-btn"
                onClick={() => handleScroll('down')}
                disabled={currentReelIndex === reels.length - 1}
              >
                ↓
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Reels;
