const config = {
  // Backend URL - change this based on environment
  BACKEND_URL: process.env.REACT_APP_BACKEND_URL || 'http://localhost:4000',

  // For production, you would set REACT_APP_BACKEND_URL to your Render backend URL
  // Example: https://your-backend-name.onrender.com
};

export default config;
