import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../App';
import VideoCard from '../components/VideoCard';
import VideoUploadModal from '../components/VideoUploadModal';
import { useNavigate } from 'react-router-dom';
import './Videos.css';
import config from '../config/config';

const Videos = () => {
  const { state } = useContext(UserContext);
  const navigate = useNavigate();
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('newest');
  const [showUploadModal, setShowUploadModal] = useState(false);

  useEffect(() => {
    fetchVideos();
  }, [searchQuery, sortBy]);

  const fetchVideos = async () => {
    try {
      setLoading(true);
      const queryString = new URLSearchParams({
        q: searchQuery,
        sort: sortBy
      }).toString();

      const response = await fetch(`${config.BACKEND_URL}/allvideos?${queryString}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('jwt')}`
        }
      });

      if (!response.ok) {
        throw new Error('Failed to fetch videos');
      }

      const data = await response.json();
      setVideos(data);
    } catch (err) {
      console.error('Error fetching videos:', err);
      setError('Failed to load videos');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSort = (e) => {
    setSortBy(e.target.value);
  };

  const handleUploadSuccess = () => {
    setShowUploadModal(false);
    navigate('/profile');
  };

  const formatDuration = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  if (error) {
    return (
      <div className="videos-container">
        <div className="error-message">
          <h2>Error Loading Videos</h2>
          <p>{error}</p>
          <button onClick={fetchVideos}>Try Again</button>
        </div>
      </div>
    );
  }

  return (
    <div className="videos-container">
      <div className="videos-header">
        <h1>Videos</h1>
        <p>Discover amazing content from creators</p>
      </div>

      <div className="videos-controls">
        <div className="search-container">
          <input
            type="text"
            placeholder="Search videos..."
            value={searchQuery}
            onChange={handleSearch}
            className="search-input"
          />
        </div>

        <div className="sort-container">
          <select 
            value={sortBy} 
            onChange={handleSort}
            className="sort-select"
          >
            <option value="newest">Newest</option>
            <option value="oldest">Oldest</option>
            <option value="mostViewed">Most Viewed</option>
            <option value="mostLiked">Most Liked</option>
          </select>
        </div>
      </div>

      {loading ? (
        <div className="loading">
          <p>Loading videos...</p>
        </div>
      ) : videos.length === 0 ? (
        <div className="no-videos">
          <p>No videos found. Try adjusting your search or filters.</p>
        </div>
      ) : (
        <div className="videos-grid">
          {videos.map(video => (
            <VideoCard
              key={video._id}
              video={video}
              onVideoUpdate={fetchVideos}
            />
          ))}
        </div>
      )}

      {showUploadModal && (
        <VideoUploadModal
          onClose={() => setShowUploadModal(false)}
          onUploadSuccess={handleUploadSuccess}
        />
      )}
    </div>
  );
};

export default Videos;
