import React, { useEffect } from "react";
import "./PostDetail.css";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

export default function PostDetail({ item, toggleDetails }) {
  const navigate = useNavigate();
  const picLink = "https://cdn-icons-png.flaticon.com/128/3177/3177440.png";

  // Debug logs
  console.log("Full post data:", item);
  console.log("Posted by user data:", item?.postedBy);
  console.log("Profile picture URL:", item?.postedBy?.Photo);

  // Toast functions
  const notifyA = (msg) => toast.error(msg);
  const notifyB = (msg) => toast.success(msg);

  // Fetch post details if postedBy is not fully populated
  useEffect(() => {
    if (item?._id && (!item.postedBy?.Photo || !item.postedBy?.name)) {
      console.log("Fetching post details for post:", item._id);
      fetch(`http://localhost:4000/post/${item._id}`, {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("jwt"),
        },
      })
        .then((res) => res.json())
        .then((result) => {
          console.log("Fetched post details:", result);
          if (result.postedBy) {
            console.log("Updating postedBy with:", result.postedBy);
            item.postedBy = result.postedBy;
          }
        })
        .catch((err) => {
          console.error("Error fetching post details:", err);
        });
    }
  }, [item]);

  const removePost = (postId) => {
    if (window.confirm("Do you really want to delete this post ?")) {
      fetch(`http://localhost:4000/deletePost/${postId}`, {
        method: "delete",
        headers: {
          Authorization: "Bearer " + localStorage.getItem("jwt"),
        },
      })
        .then((res) => res.json())
        .then((result) => {
          console.log(result);
          toggleDetails();
          navigate("/");
          notifyB(result.message);
        });
    }
  };

  return (
    <div className="showComment">
      <div className="container">
        <div className="postPic">
          <img src={item.photo} alt="" />
        </div>
        <div className="details">
          {/* card header */}
          <div
            className="card-header"
            style={{ borderBottom: "1px solid #00000029" }}
          >
            <div className="card-pic">
              <img
                src={item.postedBy?.Photo || picLink}
                alt={item.postedBy?.name}
                onError={(e) => {
                  console.log("Error loading profile picture. User data:", item.postedBy);
                  console.log("Attempted photo URL:", item.postedBy?.Photo);
                  console.log("Error details:", e);
                  e.target.src = picLink;
                }}
                style={{ width: "40px", height: "40px", borderRadius: "50%", objectFit: "cover" }}
              />
            </div>
            <h5>{item.postedBy?.name}</h5>
            <div
              className="deletePost"
              onClick={() => {
                removePost(item._id);
              }}
            >
              <span className="material-symbols-outlined">delete</span>
            </div>
          </div>

          {/* commentSection */}
          <div
            className="comment-section"
            style={{ borderBottom: "1px solid #00000029" }}
          >
            {item.comments.map((comment) => {
              return (
                <div className="comment-item">
                  <div className="comment-pic">
                    <img
                      src={comment.postedBy.Photo ? comment.postedBy.Photo : picLink}
                      alt={comment.postedBy.name}
                      style={{ width: "24px", height: "24px", borderRadius: "50%", objectFit: "cover" }}
                    />
                  </div>
                <p className="comm">
                  <span className="commenter" style={{ fontWeight: "bolder" }}>
                    {comment.postedBy.name}{" "}
                  </span>
                  <span className="commentText">{comment.comment}</span>
                </p>
                </div>
              );
            })}
          </div>

          {/* card content */}
          <div className="card-content">
            <p>{item.likes.length} Likes</p>
            <p>{item.body}</p>
          </div>

          {/* add Comment */}
          <div className="add-comment">
            <span className="material-symbols-outlined">mood</span>
            <input
              type="text"
              placeholder="Add a comment"
              //   value={comment}
              //   onChange={(e) => {
              //     setComment(e.target.value);
              //   }}
            />
            <button
              className="comment"
              //   onClick={() => {
              //     makeComment(comment, item._id);
              //     toggleComment();
              //   }}
            >
              Post
            </button>
          </div>
        </div>
      </div>
      <div
        className="close-comment"
        onClick={() => {
          toggleDetails();
        }}
      >
        <span className="material-symbols-outlined material-symbols-outlined-comment">
          Close
        </span>
      </div>
    </div>
  );
}
