import React, { useState, useEffect } from 'react';
import './AudioBook.css';

const AudioBook = () => {
  const [audiobooks, setAudiobooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchAudiobooks();
  }, []);

  const fetchAudiobooks = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/audiobooks');
      const data = await response.json();
      setAudiobooks(data.data?.audiobooks || []);
      setLoading(false);
    } catch (err) {
      setError('Failed to fetch audiobooks');
      setLoading(false);
    }
  };

  if (loading) return <div className="loading">Loading audiobooks...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="audiobook-container">
      <h1 className="audiobook-title">Audiobooks</h1>
      <div className="audiobooks-grid">
        {audiobooks.map((book) => (
          <div key={book._id} className="audiobook-card">
            <div className="audiobook-image">
              <img src={book.coverImage} alt={book.title} />
              <div className="play-button">
                <i className="material-icons">play_circle</i>
              </div>
            </div>
            <div className="audiobook-info">
              <h3>{book.title}</h3>
              <p>{book.author}</p>
              <div className="audiobook-duration">
                <i className="material-icons">schedule</i>
                <span>{book.duration}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AudioBook;
