import React, { useState, useContext } from 'react';
import { UserContext } from '../App';
import './VideoUploadModal.css';
import axios from 'axios';

const ReelUploadModal = ({ onClose, onUploadSuccess }) => {
  const { state } = useContext(UserContext);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    video: null,
    category: ''
  });
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState('');
  const [preview, setPreview] = useState({
    video: null
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    if (files) {
      const file = files[0];
      setFormData(prev => ({
        ...prev,
        [name]: file
      }));

      // Create preview for video
      if (name === 'video' && file) {
        const videoUrl = URL.createObjectURL(file);
        setPreview(prev => ({
          ...prev,
          video: videoUrl
        }));
      }
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!state) return;

    setUploading(true);
    setError('');

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('title', formData.title);
      formDataToSend.append('description', formData.description);
      formDataToSend.append('video', formData.video);
      formDataToSend.append('category', formData.category);

      const response = await axios.post('/api/reels/upload', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${localStorage.getItem('jwt')}`
        }
      });

      if (response.data) {
        onUploadSuccess();
        onClose();
      }
    } catch (err) {
      console.error('Reel upload error:', err);
      setError(err.response?.data?.error || err.message || 'Failed to upload reel. Please try again.');
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Upload Reel</h2>
          <button className="close-button" onClick={onClose}>&times;</button>
        </div>

        <form onSubmit={handleSubmit} className="upload-form">
          <div className="form-group">
            <label htmlFor="title">Title</label>
            <input
              type="text"
              id="title"
              name="title"
              value={formData.title}
              onChange={handleChange}
              required
              placeholder="Enter reel title"
              maxLength={100}
            />
          </div>

          <div className="form-group">
            <label htmlFor="description">Description</label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              required
              placeholder="Enter reel description"
              rows="4"
              maxLength={500}
            />
          </div>

          <div className="form-group">
            <label htmlFor="video">Reel Video</label>
            <input
              type="file"
              id="video"
              name="video"
              onChange={handleChange}
              accept="video/*"
              required
            />
            {preview.video && (
              <div className="preview-container">
                <video src={preview.video} controls className="video-preview" />
              </div>
            )}
          </div>

          <div className="form-group">
            <label htmlFor="category">Category</label>
            <select
              id="category"
              name="category"
              value={formData.category}
              onChange={handleChange}
              required
            >
              <option value="">Select a category</option>
              <option value="education">Education</option>
              <option value="business">Business</option>
              <option value="entertainment">Entertainment</option>
              <option value="sports-fitness">Sports/Fitness</option>
              <option value="gaming">Gaming</option>
              <option value="food-travel">Food/Travel</option>
            </select>
          </div>

          {error && <div className="error-message">{error}</div>}

          <div className="form-actions">
            <button
              type="button"
              className="cancel-button"
              onClick={onClose}
              disabled={uploading}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="submit-button"
              disabled={uploading}
            >
              {uploading ? 'Uploading...' : 'Upload Reel'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ReelUploadModal;
