import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaHeart, FaComment, FaShare } from 'react-icons/fa';
import './Reels.css';

const Reels = () => {
  const [reels, setReels] = useState([]);
  const [currentReelIndex, setCurrentReelIndex] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchReels();
  }, []);

  const fetchReels = async () => {
    try {
      const response = await axios.get('http://localhost:4000/api/reels');
      setReels(response.data);
      setLoading(false);
    } catch (err) {
      setError('Failed to fetch reels');
      setLoading(false);
    }
  };

  const handleLike = async (reelId) => {
    try {
      const response = await axios.put(`http://localhost:4000/api/reels/like/${reelId}`, {}, {
        withCredentials: true
      });
      setReels(reels.map(reel => 
        reel._id === reelId ? response.data : reel
      ));
    } catch (err) {
      console.error('Error liking reel:', err);
    }
  };

  const handleComment = async (reelId, comment) => {
    try {
      const response = await axios.post(`http://localhost:4000/api/reels/comment/${reelId}`, 
        { text: comment },
        { withCredentials: true }
      );
      setReels(reels.map(reel => 
        reel._id === reelId ? response.data : reel
      ));
    } catch (err) {
      console.error('Error commenting on reel:', err);
    }
  };

  const handleShare = (reel) => {
    navigator.clipboard.writeText(`${window.location.origin}/reels/${reel._id}`);
    alert('Link copied to clipboard!');
  };

  const handleScroll = (e) => {
    if (e.deltaY > 0 && currentReelIndex < reels.length - 1) {
      setCurrentReelIndex(prev => prev + 1);
    } else if (e.deltaY < 0 && currentReelIndex > 0) {
      setCurrentReelIndex(prev => prev - 1);
    }
  };

  if (loading) return <div className="loading">Loading reels...</div>;
  if (error) return <div className="error">{error}</div>;
  if (reels.length === 0) return <div className="no-reels">No reels available</div>;

  const currentReel = reels[currentReelIndex];

  return (
    <div className="reels-container" onWheel={handleScroll}>
      <div className="reel-video-container">
        <video
          src={currentReel.videoUrl}
          loop
          autoPlay
          muted
          playsInline
          className="reel-video"
        />

        <div className="reel-info">
          <div className="reel-header">
            <img 
              src={currentReel.postedBy.profilePic} 
              alt={currentReel.postedBy.name}
              className="profile-pic"
            />
            <span className="username">{currentReel.postedBy.name}</span>
          </div>

          <div className="reel-actions">
            <button onClick={() => handleLike(currentReel._id)}>
              <FaHeart className={currentReel.likes.includes(currentReel.postedBy._id) ? 'liked' : ''} />
              <span>{currentReel.likes.length}</span>
            </button>
            <button onClick={() => navigate(`/reels/${currentReel._id}`)}>
              <FaComment />
              <span>{currentReel.comments.length}</span>
            </button>
            <button onClick={() => handleShare(currentReel)}>
              <FaShare />
            </button>
          </div>

          <div className="reel-description">
            <p>{currentReel.description}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Reels;
